        margin-bottom: 20px;
    }
    .links {
        display: grid;
        grid-template-columns: repeat(2, minmax(150px, 1fr));
        gap: 20px;
    }

    .links a {
        width: 254px;
        height: 254px;
        text-decoration: none;
        border-radius: 8px;
        text-align: center;
        font-size: 18px;
        transition: background-color 0.3s ease;
    }
    .card {
        display: flex;
        align-items: center;
        text-align: center;
        justify-content: center;
        background: rgba(233, 182, 27, 1);
